package simpletexteditor.app;

import org.apache.commons.text.WordUtils;
import simpletexteditor.list.LinkedList;

import static simpletexteditor.app.MessageUtils.getMessage;
import static simpletexteditor.utilities.StringUtils.join;
import static simpletexteditor.utilities.StringUtils.split;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
